import React, { useEffect, useRef, useState, useCallback } from 'react';
import * as fabric from 'fabric';
import {
  Pencil, Square, Circle as CircleIcon, Triangle as TriangleIcon,
  Type, Image as ImageIcon, Eraser, MousePointer2,
  Undo2, Redo2, Trash2, Move, ZoomIn, ZoomOut,
  Palette
} from 'lucide-react';
import { useLanguage } from '../../contexts/LanguageContext';

// --- Types ---
type Tool = 'select' | 'pan' | 'pen' | 'marker' | 'rectangle' | 'circle' | 'triangle' | 'line' | 'text' | 'image' | 'eraser';

interface DrawingState {
  tool: Tool;
  strokeColor: string;
  fillColor: string;
  strokeWidth: number;
  opacity: number;
  fontFamily: string;
  activeObject: fabric.Object | null;
}

const DrawingView: React.FC = () => {
  const { t } = useLanguage();
  const containerRef = useRef<HTMLDivElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [fabricCanvas, setFabricCanvas] = useState<fabric.Canvas | null>(null);

  // State
  const [state, setState] = useState<DrawingState>({
    tool: 'select',
    strokeColor: '#000000',
    fillColor: 'transparent',
    strokeWidth: 3,
    opacity: 1,
    fontFamily: 'Inter',
    activeObject: null
  });

  const [history, setHistory] = useState<string[]>([]);
  const [historyIndex, setHistoryIndex] = useState(-1);
  const [isLoading, setIsLoading] = useState(true);
  const [isDraggingFile, setIsDraggingFile] = useState(false);

  // --- Persistence (Professional Level) ---
  const saveToLocalStorage = useCallback(() => {
    if (!fabricCanvas) return;
    try {
      const json = fabricCanvas.toJSON();
      const dataToSave = {
        version: '0.3.3',
        timestamp: new Date().toISOString(),
        canvas: json,
      };
      localStorage.setItem('planner-drawing-state', JSON.stringify(dataToSave));

      // Update History
      const newHistory = history.slice(0, historyIndex + 1);
      newHistory.push(JSON.stringify(json));
      if (newHistory.length > 50) newHistory.shift();

      setHistory(newHistory);
      setHistoryIndex(newHistory.length - 1);
    } catch (e) {
      console.error('Failed to save drawing state:', e);
    }
  }, [fabricCanvas, history, historyIndex]);

  // Failsafe: Force loading to stop after 2s to prevent infinite spinner
  useEffect(() => {
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 2000);
    return () => clearTimeout(timer);
  }, []);

  // --- Initialization ---
  useEffect(() => {
    if (!canvasRef.current || !containerRef.current) return;

    // Initialize Fabric Canvas
    const canvas = new fabric.Canvas(canvasRef.current, {
      isDrawingMode: false,
      selection: true,
      backgroundColor: '#ffffff',
      preserveObjectStacking: true,
      stopContextMenu: true,
      fireRightClick: true,
    });

    // Set initial dimensions
    canvas.setDimensions({
      width: containerRef.current.clientWidth,
      height: containerRef.current.clientHeight || window.innerHeight - 100
    });

    // Custom config
    fabric.Object.prototype.set({
      cornerColor: '#3b82f6',
      cornerStyle: 'circle',
      borderColor: '#3b82f6',
      borderScaleFactor: 1.5,
      transparentCorners: false,
      cornerSize: 10,
      padding: 5,
    });

    setFabricCanvas(canvas);

    const resizeObserver = new ResizeObserver(() => {
      if (containerRef.current) {
        canvas.setDimensions({
          width: containerRef.current.clientWidth,
          height: containerRef.current.clientHeight
        });
        canvas.renderAll();
      }
    });
    resizeObserver.observe(containerRef.current);

    return () => {
      canvas.dispose();
      resizeObserver.disconnect();
    };
  }, []);

  // --- Tool Logic ---
  useEffect(() => {
    if (!fabricCanvas) return;

    // Reset
    fabricCanvas.isDrawingMode = false;
    fabricCanvas.selection = true;
    fabricCanvas.defaultCursor = 'default';

    // Apply Tool Settings
    switch (state.tool) {
      case 'pan':
        fabricCanvas.selection = false;
        fabricCanvas.defaultCursor = 'grab';
        break;
      case 'pen':
      case 'marker':
        fabricCanvas.isDrawingMode = true;
        const brush = new fabric.PencilBrush(fabricCanvas);
        brush.color = state.strokeColor;
        brush.width = state.tool === 'marker' ? state.strokeWidth * 4 : state.strokeWidth;
        // Marker opacity simulation
        if (state.tool === 'marker') {
          brush.color = new fabric.Color(state.strokeColor).setAlpha(0.5).toRgba();
        }
        fabricCanvas.freeDrawingBrush = brush;
        break;
      case 'eraser':
        fabricCanvas.selection = true;
        fabricCanvas.defaultCursor = 'not-allowed'; // Visual cue
        break;
      default:
        // Shapes/Text handled on click/drag
        fabricCanvas.defaultCursor = 'crosshair';
        if (state.tool === 'select') fabricCanvas.defaultCursor = 'default';
        break;
    }
  }, [fabricCanvas, state.tool, state.strokeColor, state.strokeWidth]);

  // --- Selection Handling ---
  useEffect(() => {
    if (!fabricCanvas) return;

    const handleSelection = (e: any) => {
      const selected = e.selected?.[0] || null;
      setState(s => ({ ...s, activeObject: selected }));
    };

    const handleCleared = () => {
      setState(s => ({ ...s, activeObject: null }));
    };

    fabricCanvas.on('selection:created', handleSelection);
    fabricCanvas.on('selection:updated', handleSelection);
    fabricCanvas.on('selection:cleared', handleCleared);

    return () => {
      fabricCanvas.off('selection:created', handleSelection);
      fabricCanvas.off('selection:updated', handleSelection);
      fabricCanvas.off('selection:cleared', handleCleared);
    };
  }, [fabricCanvas]);

  // --- Image Upload Logic (PhD Level) ---
  const handleImageUpload = (file: File) => {
    if (!file || !fabricCanvas) return;

    const reader = new FileReader();
    reader.onload = (f) => {
      const data = f.target?.result as string;
      fabric.Image.fromURL(data).then((img) => {
        // Smart scaling to fit screen
        if (img) {
          const maxWidth = fabricCanvas.width! * 0.5;
          const maxHeight = fabricCanvas.height! * 0.5;
          const scale = Math.min(
            maxWidth / (img.width || 1),
            maxHeight / (img.height || 1),
            1
          );

          img.scale(scale);
          img.set({
            left: fabricCanvas.width! / 2,
            top: fabricCanvas.height! / 2,
            originX: 'center',
            originY: 'center',
            cornerColor: '#3b82f6',
            cornerStyle: 'circle',
          });

          fabricCanvas.add(img);
          fabricCanvas.setActiveObject(img);
          setState(s => ({ ...s, tool: 'select' }));
          // Save after adding image
          saveToLocalStorage();
        }
      });
    };
    reader.readAsDataURL(file);
  };

  const onDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDraggingFile(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      const file = e.dataTransfer.files[0];
      if (file.type.startsWith('image/')) {
        handleImageUpload(file);
      }
    }
  }, [fabricCanvas, saveToLocalStorage]);

  const onDragOver = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDraggingFile(true);
  }, []);

  const onDragLeave = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDraggingFile(false);
  }, []);


  // --- Shape Adoption (Click to Add) ---
  // Removed unused addShape helper; shape creation is handled directly in mouse events.

  // Listen for clicks to add shapes (if tool is active)
  useEffect(() => {
    if (!fabricCanvas) return;

    const handleMouseDown = (opt: any) => {
      if (state.tool === 'select' || state.tool === 'pan' || state.tool === 'pen' || state.tool === 'marker' || state.tool === 'eraser' || state.tool === 'image') return;

      // If active tool is a shape, add it at cursor
      const pointer = fabricCanvas.getPointer(opt.e);
      let shape: fabric.Object | null = null;
      const common = {
        left: pointer.x,
        top: pointer.y,
        fill: state.fillColor === 'transparent' && state.tool !== 'text' ? 'transparent' : state.fillColor,
        stroke: state.strokeColor,
        strokeWidth: state.strokeWidth,
        originX: 'center',
        originY: 'center',
      };

      switch (state.tool) {
        case 'rectangle':
          shape = new fabric.Rect({ ...(common as any), width: 100, height: 100, fill: common.fill === 'transparent' ? 'transparent' : common.fill } as any);
          break;
        case 'circle':
          shape = new fabric.Circle({ ...(common as any), radius: 50 } as any);
          break;
        case 'triangle':
          shape = new fabric.Triangle({ ...(common as any), width: 100, height: 100 } as any);
          break;
        case 'text':
          shape = new fabric.IText('Type here...', { ...(common as any), fontSize: 24, fontFamily: 'Inter', fill: state.strokeColor, strokeWidth: 0 } as any);
          break;
      }

      if (shape) {
        fabricCanvas.add(shape);
        fabricCanvas.setActiveObject(shape);
        setState(s => ({ ...s, tool: 'select' })); // Auto-switch back to select
        saveToLocalStorage();
      }
    };

    const handleClick = (opt: any) => {
      if (state.tool === 'eraser' && opt.target) {
        fabricCanvas.remove(opt.target);
        saveToLocalStorage();
      }
    }

    fabricCanvas.on('mouse:down', handleMouseDown);
    fabricCanvas.on('mouse:down', handleClick); // Eraser logic

    return () => {
      fabricCanvas.off('mouse:down', handleMouseDown);
      fabricCanvas.off('mouse:down', handleClick);
    };
  }, [fabricCanvas, state.tool, state.fillColor, state.strokeColor, state.strokeWidth, saveToLocalStorage]);


  // --- Zoom & Pan Logic (Infinite Canvas Feel) ---
  useEffect(() => {
    if (!fabricCanvas) return;

    const handleWheel = (opt: any) => {
      if (opt.e.ctrlKey || opt.e.metaKey) {
        // Zoom
        const delta = opt.e.deltaY;
        let zoom = fabricCanvas.getZoom();
        zoom *= 0.999 ** delta;
        if (zoom > 20) zoom = 20;
        if (zoom < 0.01) zoom = 0.01;
        fabricCanvas.zoomToPoint({ x: opt.e.offsetX, y: opt.e.offsetY }, zoom);
        opt.e.preventDefault();
        opt.e.stopPropagation();
      } else if (state.tool === 'pan' || opt.e.shiftKey) {
        // Pan
        const vpt = fabricCanvas.viewportTransform;
        if (vpt) {
          vpt[4] -= opt.e.deltaX;
          vpt[5] -= opt.e.deltaY;
          fabricCanvas.requestRenderAll();
        }
        opt.e.preventDefault();
        opt.e.stopPropagation();
      }
    };

    // Middle Click Pan
    let isDragging = false;
    let lastPosX = 0;
    let lastPosY = 0;

    const handleMouseDown = (opt: any) => {
      const evt = opt.e;
      if (state.tool === 'pan' || evt.button === 1 || (evt.altKey)) {
        isDragging = true;
        fabricCanvas.selection = false;
        lastPosX = evt.clientX;
        lastPosY = evt.clientY;
        fabricCanvas.defaultCursor = 'grabbing';
      }
    };

    const handleMouseMove = (opt: any) => {
      if (isDragging) {
        const e = opt.e;
        const vpt = fabricCanvas.viewportTransform;
        if (vpt) {
          vpt[4] += e.clientX - lastPosX;
          vpt[5] += e.clientY - lastPosY;
          fabricCanvas.requestRenderAll();
          lastPosX = e.clientX;
          lastPosY = e.clientY;
        }
      }
    };

    const handleMouseUp = () => {
      if (isDragging) {
        isDragging = false;
        fabricCanvas.selection = true;
        fabricCanvas.defaultCursor = 'default';
        if (state.tool !== 'pan') {
          fabricCanvas.selection = true;
        }
      }
    };

    fabricCanvas.on('mouse:wheel', handleWheel);
    fabricCanvas.on('mouse:down', handleMouseDown);
    fabricCanvas.on('mouse:move', handleMouseMove);
    fabricCanvas.on('mouse:up', handleMouseUp);

    return () => {
      fabricCanvas.off('mouse:wheel', handleWheel);
      fabricCanvas.off('mouse:down', handleMouseDown);
      fabricCanvas.off('mouse:move', handleMouseMove);
      fabricCanvas.off('mouse:up', handleMouseUp);
    };
  }, [fabricCanvas, state.tool]);

  // --- Persistence moved earlier ---

  // Debounced Save on changes
  useEffect(() => {
    if (!fabricCanvas) return;

    const handleObjectModified = () => saveToLocalStorage();
    const handlePathCreated = () => saveToLocalStorage();
    const handleObjectAdded = () => saveToLocalStorage();

    fabricCanvas.on('object:modified', handleObjectModified);
    fabricCanvas.on('path:created', handlePathCreated);
    fabricCanvas.on('object:added', handleObjectAdded);
    fabricCanvas.on('object:removed', handleObjectModified);

    return () => {
      fabricCanvas.off('object:modified', handleObjectModified);
      fabricCanvas.off('path:created', handlePathCreated);
      fabricCanvas.off('object:added', handleObjectAdded);
      fabricCanvas.off('object:removed', handleObjectModified);
    };
  }, [fabricCanvas, saveToLocalStorage]);

  // Restore from Storage (Robust & Safe)
  useEffect(() => {
    if (!fabricCanvas) return;

    const loadState = async () => {
      try {
        const saved = localStorage.getItem('planner-drawing-state');
        const legacy = localStorage.getItem('drawing_draft');

        let jsonData = null;

        if (saved) {
          const parsed = JSON.parse(saved);
          jsonData = parsed.canvas || parsed;
        } else if (legacy) {
          jsonData = JSON.parse(legacy);
        }

        if (jsonData) {
          await new Promise<void>((resolve) => {
            fabricCanvas.loadFromJSON(jsonData, () => {
              fabricCanvas.renderAll();
              resolve();
            });
          });

          // Force layout recalculation and second render frame
          // This is critical for objects to appear immediately
          fabricCanvas.setDimensions({
            width: containerRef.current?.clientWidth || 800,
            height: containerRef.current?.clientHeight || 600
          });
          fabricCanvas.requestRenderAll();

          setHistory([JSON.stringify(jsonData)]);
          setHistoryIndex(0);
          console.log('[DrawingView] State restored successfully');
        } else {
          setHistory([JSON.stringify(fabricCanvas.toJSON())]);
          setHistoryIndex(0);
        }
      } catch (error) {
        console.error('[DrawingView] Failed to restore state:', error);
        fabricCanvas.clear();
      } finally {
        setIsLoading(false);
        // Triple check render after loading flag is off
        setTimeout(() => fabricCanvas?.requestRenderAll(), 100);
      }
    };

    // Small delay to ensure render
    setTimeout(loadState, 100);

  }, [fabricCanvas]);

  // --- Actions ---
  const performUndo = () => {
    if (historyIndex <= 0 || !fabricCanvas) return;
    const prev = history[historyIndex - 1];
    setHistoryIndex(historyIndex - 1);
    fabricCanvas.loadFromJSON(prev, () => fabricCanvas.renderAll());
  };

  const performRedo = () => {
    if (historyIndex >= history.length - 1 || !fabricCanvas) return;
    const next = history[historyIndex + 1];
    setHistoryIndex(historyIndex + 1);
    fabricCanvas.loadFromJSON(next, () => fabricCanvas.renderAll());
  };

  const clearCanvas = () => {
    if (!fabricCanvas) return;
    if (confirm('Clear entire canvas?')) {
      fabricCanvas.clear();
      fabricCanvas.backgroundColor = '#ffffff';
      saveToLocalStorage();
    }
  };

  const deleteSelected = () => {
    const active = fabricCanvas?.getActiveObjects();
    if (active?.length) {
      fabricCanvas?.discardActiveObject();
      active.forEach(obj => fabricCanvas?.remove(obj));
      saveToLocalStorage();
    }
  };

  // --- UI Components ---

  return (
    <div
      className="h-full flex flex-col relative bg-gray-50 dark:bg-gray-900 overflow-hidden select-none"
      onDrop={onDrop}
      onDragOver={onDragOver}
      onDragLeave={onDragLeave}
    >
      {/* Main Canvas Container */}
      <div ref={containerRef} className="flex-1 w-full h-full relative shadow-inner">
        <canvas ref={canvasRef} className="absolute inset-0 z-10" />

        {/* Drag Overlay */}
        {isDraggingFile && (
          <div className="absolute inset-0 z-50 flex items-center justify-center bg-primary-500/10 backdrop-blur-sm border-4 border-dashed border-primary-500 rounded-lg m-4 pointer-events-none">
            <div className="text-primary-600 dark:text-primary-400 font-bold text-2xl animate-pulse">
              Drop Image Here
            </div>
          </div>
        )}

        {/* Loading Shield */}
        {isLoading && (
          <div className="absolute inset-0 z-50 flex items-center justify-center bg-white/80 dark:bg-gray-900/80 backdrop-blur-sm">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
          </div>
        )}
      </div>

      {/* Hidden File Input */}
      <input
        type="file"
        ref={fileInputRef}
        onChange={(e) => {
          if (e.target.files?.[0]) handleImageUpload(e.target.files[0]);
        }}
        className="hidden"
        accept="image/*"
      />

      {/* --- Floating Toolbar (Center Top) - PhD Design --- */}
      <div className="absolute top-6 left-1/2 transform -translate-x-1/2 z-40 bg-white/80 dark:bg-gray-900/80 backdrop-blur-2xl border border-white/20 dark:border-white/10 shadow-2xl shadow-black/10 rounded-2xl p-2 flex items-center gap-1 ring-1 ring-black/5 dark:ring-white/5 mx-auto max-w-[90vw] overflow-x-auto no-scrollbar">
        {[
          { id: 'select', icon: MousePointer2, label: 'Select' },
          { id: 'pan', icon: Move, label: 'Pan' },
          { id: 'sep1', type: 'separator' },
          { id: 'pen', icon: Pencil, label: 'Pen' },
          { id: 'text', icon: Type, label: 'Text' },
          { id: 'image', icon: ImageIcon, label: 'Image', action: () => fileInputRef.current?.click() },
          { id: 'sep2', type: 'separator' },
          { id: 'rectangle', icon: Square, label: 'Rectangle' },
          { id: 'circle', icon: CircleIcon, label: 'Circle' },
          { id: 'triangle', icon: TriangleIcon, label: 'Triangle' },
          { id: 'sep3', type: 'separator' },
          { id: 'eraser', icon: Eraser, label: 'Eraser' },
        ].map((item: any, i) => {
          if (item.type === 'separator') return <div key={`sep-${i}`} className="w-px h-6 bg-gray-200 dark:bg-gray-700 mx-1 shrink-0" />;
          return (
            <button
              key={item.id}
              onClick={() => item.action ? item.action() : setState(s => ({ ...s, tool: item.id }))}
              className={`
                p-2.5 rounded-xl transition-all duration-300 group relative shrink-0
                ${state.tool === item.id && !item.action
                  ? 'bg-gradient-to-br from-primary-500/20 to-primary-600/20 text-primary-600 dark:text-primary-300 ring-1 ring-primary-500/30'
                  : 'text-gray-500 hover:bg-gray-100/50 dark:text-gray-400 dark:hover:bg-white/5 hover:text-gray-900 dark:hover:text-white'}
              `}
              title={item.label}
            >
              <item.icon size={20} strokeWidth={state.tool === item.id ? 2.5 : 2} />
              {/* Tooltip */}
              <span className="absolute top-full mt-3 left-1/2 -translate-x-1/2 px-2.5 py-1.5 bg-gray-900/95 dark:bg-black/95 text-white text-[10px] font-medium rounded-lg opacity-0 group-hover:opacity-100 transition-all duration-200 translate-y-1 group-hover:translate-y-0 whitespace-nowrap pointer-events-none shadow-xl border border-white/10 z-50">
                {item.label}
              </span>
            </button>
          )
        })}
      </div>

      {/* --- Context Property Bar (Left Side) - PhD Design --- */}
      <div className="absolute top-1/2 left-6 transform -translate-y-1/2 z-40 flex flex-col gap-4">
        {/* Colors */}
        <div className="bg-white/80 dark:bg-gray-900/80 backdrop-blur-2xl border border-white/20 dark:border-white/10 shadow-2xl shadow-black/10 rounded-2xl p-4 flex flex-col gap-3 ring-1 ring-black/5 dark:ring-white/5 animate-fade-in-left">
          <label className="text-[10px] font-bold text-gray-400 uppercase tracking-widest">{t('drawing.stroke') || 'Stroke'}</label>
          <div className="grid grid-cols-2 gap-2">
            {['#000000', '#ef4444', '#22c55e', '#3b82f6', '#f59e0b', '#8b5cf6'].map(color => (
              <button
                key={color}
                onClick={() => {
                  setState(s => ({ ...s, strokeColor: color }));
                  if (fabricCanvas?.getActiveObject()) {
                    fabricCanvas.getActiveObject()?.set({ stroke: color, fill: state.tool === 'text' ? color : state.fillColor });
                    if (state.tool === 'text') fabricCanvas.getActiveObject()?.set({ fill: color });
                    fabricCanvas.requestRenderAll();
                    saveToLocalStorage();
                  }
                }}
                className={`w-8 h-8 rounded-full border-[3px] transition-all duration-300 hover:scale-110 shadow-sm ${state.strokeColor === color ? 'border-gray-900 dark:border-white scale-110 ring-2 ring-primary-500/20' : 'border-transparent'}`}
                style={{ backgroundColor: color }}
              />
            ))}
            <button className="w-8 h-8 rounded-full border-2 border-gray-200 dark:border-gray-700 flex items-center justify-center text-gray-400 hover:bg-gray-50 dark:hover:bg-gray-800 transition-colors">
              <Palette size={14} />
            </button>
          </div>

          <label className="text-[10px] font-bold text-gray-400 uppercase tracking-widest mt-2">{t('drawing.width') || 'Width'}</label>
          <input
            type="range" min="1" max="20"
            value={state.strokeWidth}
            onChange={(e) => {
              const w = parseInt(e.target.value);
              setState(s => ({ ...s, strokeWidth: w }));
              if (fabricCanvas?.getActiveObject()) {
                fabricCanvas.getActiveObject()?.set({ strokeWidth: w });
                fabricCanvas.requestRenderAll();
                saveToLocalStorage();
              }
            }}
            className="accent-primary-600 h-1.5 bg-gray-200 dark:bg-gray-700 rounded-full appearance-none cursor-pointer"
          />
        </div>

        {/* Actions */}
        <div className="bg-white/80 dark:bg-gray-900/80 backdrop-blur-2xl border border-white/20 dark:border-white/10 shadow-2xl shadow-black/10 rounded-2xl p-2 flex flex-col gap-1 ring-1 ring-black/5 dark:ring-white/5 animate-fade-in-left delay-100">
          <button onClick={performUndo} disabled={historyIndex <= 0} className="p-2.5 rounded-xl text-gray-500 hover:bg-gray-100 dark:hover:bg-white/5 disabled:opacity-30 transition-colors">
            <Undo2 size={20} />
          </button>
          <button onClick={performRedo} disabled={historyIndex >= history.length - 1} className="p-2.5 rounded-xl text-gray-500 hover:bg-gray-100 dark:hover:bg-white/5 disabled:opacity-30 transition-colors">
            <Redo2 size={20} />
          </button>
          <div className="h-px bg-gray-200 dark:bg-gray-700 mx-2 my-1" />
          <button onClick={deleteSelected} disabled={!state.activeObject} className="p-2.5 rounded-xl text-red-500 hover:bg-red-50 dark:hover:bg-red-900/20 disabled:opacity-30 transition-colors">
            <Trash2 size={20} />
          </button>
        </div>
      </div>

      {/* --- Zoom Controls (Bottom Right) --- */}
      <div className="absolute bottom-6 right-6 z-40 bg-white/80 dark:bg-gray-900/80 backdrop-blur-2xl border border-white/20 dark:border-white/10 shadow-2xl shadow-black/10 rounded-2xl p-2 flex items-center gap-2 ring-1 ring-black/5 dark:ring-white/5">
        <button onClick={() => {
          const zoom = (fabricCanvas?.getZoom() || 1) * 0.9;
          fabricCanvas?.zoomToPoint({ x: fabricCanvas.width! / 2, y: fabricCanvas.height! / 2 }, zoom);
        }} className="p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-white/5 text-gray-500 transition-colors">
          <ZoomOut size={18} />
        </button>
        <span className="text-xs font-mono font-medium text-gray-500 dark:text-gray-400 w-12 text-center select-none">
          {Math.round((fabricCanvas?.getZoom() || 1) * 100)}%
        </span>
        <button onClick={() => {
          const zoom = (fabricCanvas?.getZoom() || 1) * 1.1;
          fabricCanvas?.zoomToPoint({ x: fabricCanvas.width! / 2, y: fabricCanvas.height! / 2 }, zoom);
        }} className="p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-white/5 text-gray-500 transition-colors">
          <ZoomIn size={18} />
        </button>
        <div className="w-px h-6 bg-gray-300 dark:bg-gray-700 mx-1" />
        <button onClick={clearCanvas} className="p-2 rounded-lg hover:bg-red-50 dark:hover:bg-red-900/20 text-red-500 text-[10px] font-bold uppercase tracking-wider transition-colors">
          Clear
        </button>
      </div>
    </div>
  );
};

export default DrawingView;

import React, { useState } from 'react';
import { Plus, Target, Edit2, Trash2, Calendar, TrendingUp, CheckCircle, Play, Pause, RotateCcw } from 'lucide-react';
import { useData } from '../../contexts/DataContext';
import { Goal } from '../../types/planner';
import LinkifiedText from '../common/LinkifiedText';
import { useLanguage } from '../../contexts/LanguageContext';

const GoalsView: React.FC = () => {
  const { goals, addGoal, updateGoal, deleteGoal } = useData();
  const { t } = useLanguage();
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingId, setEditingId] = useState<string | null>(null);
  const [filterStatus, setFilterStatus] = useState<string>('all');
  const [newGoal, setNewGoal] = useState({
    title: '',
    description: '',
    targetDate: '',
    progress: 0,
    status: 'not-started' as 'not-started' | 'in-progress' | 'paused' | 'completed',
  });

  const filteredGoals = goals.filter(goal => {
    if (filterStatus === 'all') return true;
    return goal.status === filterStatus;
  }).sort((a, b) => new Date(a.targetDate).getTime() - new Date(b.targetDate).getTime());

  const goalStats = {
    total: goals.length,
    completed: goals.filter(g => g.status === 'completed').length,
    inProgress: goals.filter(g => g.status === 'in-progress').length,
    notStarted: goals.filter(g => g.status === 'not-started').length,
    paused: goals.filter(g => g.status === 'paused').length,
  };

  const completionRate = goalStats.total > 0 ? (goalStats.completed / goalStats.total) * 100 : 0;

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (editingId) {
      updateGoal(editingId, {
        title: newGoal.title,
        description: newGoal.description,
        targetDate: new Date(newGoal.targetDate),
        progress: newGoal.progress,
        status: newGoal.status,
      });
      setEditingId(null);
    } else {
      addGoal({
        title: newGoal.title,
        description: newGoal.description,
        targetDate: new Date(newGoal.targetDate),
        progress: newGoal.progress,
        status: newGoal.status,
      });
    }

    setNewGoal({ title: '', description: '', targetDate: '', progress: 0, status: 'not-started' });
    setShowAddForm(false);
  };

  const handleEdit = (goal: Goal) => {
    setNewGoal({
      title: goal.title,
      description: goal.description,
      targetDate: goal.targetDate.toISOString().split('T')[0],
      progress: goal.progress,
      status: goal.status,
    });
    setEditingId(goal.id);
    setShowAddForm(true);
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'completed': return <CheckCircle className="text-green-500" size={20} />;
      case 'in-progress': return <Play className="text-blue-500" size={20} />;
      case 'paused': return <Pause className="text-yellow-500" size={20} />;
      default: return <RotateCcw className="text-gray-500" size={20} />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed': return 'border-l-green-500 bg-green-50 dark:bg-green-900/10';
      case 'in-progress': return 'border-l-blue-500 bg-blue-50 dark:bg-blue-900/10';
      case 'paused': return 'border-l-yellow-500 bg-yellow-50 dark:bg-yellow-900/10';
      default: return 'border-l-gray-500 bg-gray-50 dark:bg-gray-900/10';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'completed': return t('goals.filterCompleted');
      case 'in-progress': return t('goals.filterInProgress');
      case 'paused': return t('goals.filterPaused');
      default: return t('goals.filterNotStarted');
    }
  };

  const getDaysUntilTarget = (targetDate: Date) => {
    const today = new Date();
    const target = new Date(targetDate);
    const diffTime = target.getTime() - today.getTime();
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return diffDays;
  };

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <div className="mb-8">
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4 mb-6">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white flex items-center gap-3">
              <Target className="text-teal-500" size={32} />
              {t('goals.title')}
            </h1>
            <p className="text-gray-600 dark:text-gray-400 mt-2">
              {t('goals.subtitle')}
            </p>
          </div>

          <button
            onClick={() => setShowAddForm(true)}
            className="bg-teal-500 hover:bg-teal-600 text-white px-6 py-3 rounded-lg flex items-center gap-2 transition-colors duration-200 shadow-md hover:shadow-lg"
          >
            <Plus size={20} />
            {t('goals.newGoal')}
          </button>
        </div>

        {/* Statistics */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
          <div className="bg-gradient-to-r from-teal-500 to-blue-500 rounded-xl p-6 text-white">
            <div className="flex items-center justify-between">
              <div>
                <div className="text-2xl font-bold">{goalStats.total}</div>
                <div className="text-sm opacity-90">{t('goals.totalGoals')}</div>
              </div>
              <Target size={24} className="opacity-80" />
            </div>
          </div>

          <div className="bg-gradient-to-r from-green-500 to-teal-500 rounded-xl p-6 text-white">
            <div className="flex items-center justify-between">
              <div>
                <div className="text-2xl font-bold">{Math.round(completionRate)}%</div>
                <div className="text-sm opacity-90">{t('goals.completionRate')}</div>
              </div>
              <TrendingUp size={24} className="opacity-80" />
            </div>
          </div>

          <div className="bg-gradient-to-r from-blue-500 to-purple-500 rounded-xl p-6 text-white">
            <div className="flex items-center justify-between">
              <div>
                <div className="text-2xl font-bold">{goalStats.inProgress}</div>
                <div className="text-sm opacity-90">{t('goals.active')}</div>
              </div>
              <Play size={24} className="opacity-80" />
            </div>
          </div>

          <div className="bg-gradient-to-r from-green-600 to-green-500 rounded-xl p-6 text-white">
            <div className="flex items-center justify-between">
              <div>
                <div className="text-2xl font-bold">{goalStats.completed}</div>
                <div className="text-sm opacity-90">{t('goals.achieved')}</div>
              </div>
              <CheckCircle size={24} className="opacity-80" />
            </div>
          </div>
        </div>

        {/* Filter */}
        <div className="mb-6">
          <select
            value={filterStatus}
            onChange={(e) => setFilterStatus(e.target.value)}
            className="px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-2 focus:ring-teal-500"
          >
            <option value="all">{t('goals.filterAll')}</option>
            <option value="not-started">{t('goals.filterNotStarted')}</option>
            <option value="in-progress">{t('goals.filterInProgress')}</option>
            <option value="paused">{t('goals.filterPaused')}</option>
            <option value="completed">{t('goals.filterCompleted')}</option>
          </select>
        </div>
      </div>

      {showAddForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white dark:bg-gray-800 rounded-xl p-6 w-full max-w-md shadow-2xl">
            <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">
              {editingId ? t('goals.editGoal') : t('goals.createGoal')}
            </h3>

            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  {t('goals.goalTitle')}
                </label>
                <input
                  type="text"
                  value={newGoal.title}
                  onChange={(e) => setNewGoal({ ...newGoal, title: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:ring-2 focus:ring-teal-500"
                  required
                  placeholder={t('goals.placeholderTitle')}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  {t('goals.goalDescription')}
                </label>
                <textarea
                  value={newGoal.description}
                  onChange={(e) => setNewGoal({ ...newGoal, description: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:ring-2 focus:ring-teal-500"
                  rows={3}
                  placeholder={t('goals.placeholderDesc')}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  {t('goals.targetDate')}
                </label>
                <input
                  type="date"
                  value={newGoal.targetDate}
                  onChange={(e) => setNewGoal({ ...newGoal, targetDate: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:ring-2 focus:ring-teal-500"
                  required
                />
              </div>

              <div className="grid grid-cols-2 gap-3">
                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                    {t('goals.progress')} (%)
                  </label>
                  <input
                    type="number"
                    min="0"
                    max="100"
                    value={newGoal.progress}
                    onChange={(e) => setNewGoal({ ...newGoal, progress: parseInt(e.target.value) })}
                    className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:ring-2 focus:ring-teal-500"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                    {t('goals.status')}
                  </label>
                  <select
                    value={newGoal.status}
                    onChange={(e) => setNewGoal({ ...newGoal, status: e.target.value as any })}
                    className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:ring-2 focus:ring-teal-500"
                  >
                    <option value="not-started">{t('goals.filterNotStarted')}</option>
                    <option value="in-progress">{t('goals.filterInProgress')}</option>
                    <option value="paused">{t('goals.filterPaused')}</option>
                    <option value="completed">{t('goals.filterCompleted')}</option>
                  </select>
                </div>
              </div>

              <div className="flex gap-3 pt-4">
                <button
                  type="submit"
                  className="flex-1 bg-teal-500 hover:bg-teal-600 text-white py-2 px-4 rounded-lg transition-colors duration-200"
                >
                  {editingId ? t('common.update') : t('common.save')}
                </button>
                <button
                  type="button"
                  onClick={() => {
                    setShowAddForm(false);
                    setEditingId(null);
                    setNewGoal({ title: '', description: '', targetDate: '', progress: 0, status: 'not-started' });
                  }}
                  className="flex-1 bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded-lg transition-colors duration-200"
                >
                  {t('common.cancel')}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {filteredGoals.length === 0 ? (
        <div className="text-center py-12">
          <Target className="mx-auto text-gray-400 dark:text-gray-500 mb-4" size={48} />
          <p className="text-gray-500 dark:text-gray-400 mb-4">
            {filterStatus === 'all' ? t('goals.noGoalsDefined') : t('goals.noGoalsStatus')}
          </p>
          <button
            onClick={() => setShowAddForm(true)}
            className="bg-teal-500 hover:bg-teal-600 text-white px-6 py-2 rounded-lg transition-colors duration-200"
          >
            {t('goals.defineFirstGoal')}
          </button>
        </div>
      ) : (
        <div className="space-y-6">
          {filteredGoals.map((goal) => {
            const daysUntilTarget = getDaysUntilTarget(goal.targetDate);
            const isOverdue = daysUntilTarget < 0 && goal.status !== 'completed';

            return (
              <div
                key={goal.id}
                className={`p-6 rounded-xl border-l-4 ${getStatusColor(goal.status)} transition-all duration-200 hover:shadow-lg bg-white dark:bg-gray-800`}
              >
                <div className="flex items-start justify-between mb-4">
                  <div className="flex-1">
                    <div className="flex items-center gap-3 mb-2">
                      {getStatusIcon(goal.status)}
                      <h3 className="text-xl font-bold text-gray-900 dark:text-white">
                        {goal.title}
                      </h3>
                    </div>

                    {goal.description && (
                      <LinkifiedText
                        text={goal.description}
                        className="text-gray-700 dark:text-gray-300 mb-4 leading-relaxed"
                      />
                    )}

                    <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 mb-4">
                      <div className="flex items-center gap-2 text-sm text-gray-600 dark:text-gray-400">
                        <Calendar size={16} />
                        <span>
                          Target: {new Date(goal.targetDate).toLocaleDateString('en-US')}
                        </span>
                      </div>

                      <div className="flex items-center gap-2 text-sm">
                        <TrendingUp size={16} />
                        <span className={isOverdue ? 'text-red-600 dark:text-red-400' : 'text-gray-600 dark:text-gray-400'}>
                          {daysUntilTarget > 0 ? `${daysUntilTarget} ${t('goals.daysLeft')}` :
                            daysUntilTarget === 0 ? t('goals.dueToday') :
                              `${Math.abs(daysUntilTarget)} ${t('goals.overdue')}`}
                        </span>
                      </div>

                      <div className="flex items-center gap-2">
                        <span className={`text-sm px-3 py-1 rounded-full font-medium ${goal.status === 'completed' ? 'bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-400' :
                          goal.status === 'in-progress' ? 'bg-blue-100 text-blue-800 dark:bg-blue-900/20 dark:text-blue-400' :
                            goal.status === 'paused' ? 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900/20 dark:text-yellow-400' :
                              'bg-gray-100 text-gray-800 dark:bg-gray-900/20 dark:text-gray-400'
                          }`}>
                          {getStatusText(goal.status)}
                        </span>
                      </div>
                    </div>

                    {/* Progress Bar */}
                    <div className="mb-4">
                      <div className="flex justify-between text-sm text-gray-600 dark:text-gray-400 mb-2">
                        <span>{t('goals.progress')}</span>
                        <span>{goal.progress}%</span>
                      </div>
                      <div className="w-full bg-gray-200 dark:bg-gray-600 rounded-full h-3">
                        <div
                          className="bg-gradient-to-r from-teal-500 to-blue-500 h-3 rounded-full transition-all duration-500"
                          style={{ width: `${goal.progress}%` }}
                        />
                      </div>
                    </div>

                    {/* Quick Progress Actions */}
                    <div className="flex gap-2 mb-4">
                      <button
                        onClick={() => updateGoal(goal.id, { progress: Math.max(0, goal.progress - 10) })}
                        className="px-3 py-1 text-sm bg-gray-100 dark:bg-gray-700 text-gray-700 dark:text-gray-300 rounded-lg hover:bg-gray-200 dark:hover:bg-gray-600 transition-colors duration-200"
                        disabled={goal.progress <= 0}
                      >
                        -10%
                      </button>
                      <button
                        onClick={() => {
                          const newProgress = Math.min(100, goal.progress + 10);
                          const newStatus = newProgress === 100 ? 'completed' :
                            newProgress > 0 && goal.status === 'not-started' ? 'in-progress' :
                              goal.status;
                          updateGoal(goal.id, { progress: newProgress, status: newStatus });
                        }}
                        className="px-3 py-1 text-sm bg-teal-100 dark:bg-teal-900/20 text-teal-700 dark:text-teal-400 rounded-lg hover:bg-teal-200 dark:hover:bg-teal-900/30 transition-colors duration-200"
                        disabled={goal.progress >= 100}
                      >
                        +10%
                      </button>
                      <button
                        onClick={() => {
                          const newProgress = Math.max(0, goal.progress - 10);
                          const newStatus = newProgress === 0 ? 'not-started' :
                            newProgress < 100 && goal.status === 'completed' ? 'in-progress' :
                              goal.status;
                          updateGoal(goal.id, { progress: newProgress, status: newStatus });
                        }}
                        className="px-3 py-1 text-sm bg-orange-100 dark:bg-orange-900/20 text-orange-700 dark:text-orange-400 rounded-lg hover:bg-orange-200 dark:hover:bg-orange-900/30 transition-colors duration-200"
                        disabled={goal.progress <= 0}
                      >
                        Back -10%
                      </button>
                      {goal.progress < 100 && goal.status !== 'completed' && (
                        <button
                          onClick={() => updateGoal(goal.id, { progress: 100, status: 'completed' })}
                          className="px-3 py-1 text-sm bg-green-100 dark:bg-green-900/20 text-green-700 dark:text-green-400 rounded-lg hover:bg-green-200 dark:hover:bg-green-900/30 transition-colors duration-200"
                        >
                          Complete
                        </button>
                      )}
                    </div>
                  </div>

                  <div className="flex items-center gap-2 ml-4">
                    <button
                      onClick={() => handleEdit(goal)}
                      className="p-2 text-gray-500 hover:text-blue-500 hover:bg-blue-50 dark:hover:bg-blue-900/20 rounded-lg transition-all duration-200"
                    >
                      <Edit2 size={18} />
                    </button>
                    <button
                      onClick={() => deleteGoal(goal.id)}
                      className="p-2 text-gray-500 hover:text-red-500 hover:bg-red-50 dark:hover:bg-red-900/20 rounded-lg transition-all duration-200"
                    >
                      <Trash2 size={18} />
                    </button>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default GoalsView;

import React, { useState } from 'react';
import { Plus, Clock, CheckCircle, Circle, Edit2, Trash2 } from 'lucide-react';
import { useData } from '../../contexts/DataContext';
import { PlanItem } from '../../types/planner';
import LinkifiedText from '../common/LinkifiedText';
import { useLanguage } from '../../contexts/LanguageContext';

const HourlyView: React.FC = () => {
  const { plans, addPlan, updatePlan, deletePlan } = useData();
  const { t, language } = useLanguage();
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingId, setEditingId] = useState<string | null>(null);
  const [newPlan, setNewPlan] = useState({
    title: '',
    description: '',
    startTime: '',
    endTime: '',
    priority: 'medium' as 'low' | 'medium' | 'high',
  });

  // const today = new Date().toISOString().split('T')[0]; // Removed unused variable
  const selectedDateStr = selectedDate.toISOString().split('T')[0];

  const hours = Array.from({ length: 24 }, (_, i) => {
    const hour = i.toString().padStart(2, '0');
    return `${hour}:00`;
  });

  const dayPlans = plans.filter(plan =>
    plan.date.toISOString().split('T')[0] === selectedDateStr
  ).sort((a, b) => {
    if (a.startTime && b.startTime) {
      return a.startTime.getTime() - b.startTime.getTime();
    }
    return 0;
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const startDateTime = newPlan.startTime ? new Date(`${selectedDateStr}T${newPlan.startTime}`) : undefined;
    const endDateTime = newPlan.endTime ? new Date(`${selectedDateStr}T${newPlan.endTime}`) : undefined;

    if (editingId) {
      updatePlan(editingId, {
        title: newPlan.title,
        description: newPlan.description,
        startTime: startDateTime,
        endTime: endDateTime,
        priority: newPlan.priority,
      });
      setEditingId(null);
    } else {
      addPlan({
        title: newPlan.title,
        description: newPlan.description,
        startTime: startDateTime,
        endTime: endDateTime,
        date: selectedDate,
        completed: false,
        priority: newPlan.priority,
        linkedNotes: [],
      });
    }

    setNewPlan({ title: '', description: '', startTime: '', endTime: '', priority: 'medium' });
    setShowAddForm(false);
  };

  const handleEdit = (plan: PlanItem) => {
    setNewPlan({
      title: plan.title,
      description: plan.description,
      startTime: plan.startTime ? plan.startTime.toTimeString().substr(0, 5) : '',
      endTime: plan.endTime ? plan.endTime.toTimeString().substr(0, 5) : '',
      priority: plan.priority,
    });
    setEditingId(plan.id);
    setShowAddForm(true);
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high': return 'border-l-red-500 bg-red-50 dark:bg-red-900/10';
      case 'medium': return 'border-l-yellow-500 bg-yellow-50 dark:bg-yellow-900/10';
      case 'low': return 'border-l-green-500 bg-green-50 dark:bg-green-900/10';
      default: return 'border-l-gray-500 bg-gray-50 dark:bg-gray-900/10';
    }
  };

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <div className="mb-8">
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4 mb-6">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white flex items-center gap-3">
              <Clock className="text-blue-500" size={32} />
              {t('hourly.title')}
            </h1>
            <p className="text-gray-600 dark:text-gray-400 mt-2">
              {t('hourly.subtitle')}
            </p>
          </div>

          <button
            onClick={() => setShowAddForm(true)}
            className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-3 rounded-lg flex items-center gap-2 transition-colors duration-200 shadow-md hover:shadow-lg"
          >
            <Plus size={20} />
            {t('hourly.newTimeBlock')}
          </button>
        </div>

        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
            {t('hourly.selectDate')}
          </label>
          <input
            type="date"
            value={selectedDateStr}
            onChange={(e) => setSelectedDate(new Date(e.target.value))}
            className="px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          />
        </div>
      </div>

      {showAddForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white dark:bg-gray-800 rounded-xl p-6 w-full max-w-md shadow-2xl">
            <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">
              {editingId ? t('hourly.editBlock') : t('hourly.addBlock')}
            </h3>

            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  {t('hourly.titleLabel')}
                </label>
                <input
                  type="text"
                  value={newPlan.title}
                  onChange={(e) => setNewPlan({ ...newPlan, title: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:ring-2 focus:ring-blue-500"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  {t('hourly.descriptionLabel')}
                </label>
                <textarea
                  value={newPlan.description}
                  onChange={(e) => setNewPlan({ ...newPlan, description: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:ring-2 focus:ring-blue-500"
                  rows={3}
                />
              </div>

              <div className="grid grid-cols-2 gap-3">
                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                    {t('hourly.startTimeLabel')}
                  </label>
                  <input
                    type="time"
                    value={newPlan.startTime}
                    onChange={(e) => setNewPlan({ ...newPlan, startTime: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:ring-2 focus:ring-blue-500"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                    {t('hourly.endTimeLabel')}
                  </label>
                  <input
                    type="time"
                    value={newPlan.endTime}
                    onChange={(e) => setNewPlan({ ...newPlan, endTime: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:ring-2 focus:ring-blue-500"
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  {t('hourly.priorityLabel')}
                </label>
                <select
                  value={newPlan.priority}
                  onChange={(e) => setNewPlan({ ...newPlan, priority: e.target.value as any })}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:ring-2 focus:ring-blue-500"
                >
                  <option value="low">{t('hourly.lowPriority')}</option>
                  <option value="medium">{t('hourly.mediumPriority')}</option>
                  <option value="high">{t('hourly.highPriority')}</option>
                </select>
              </div>

              <div className="flex gap-3 pt-4">
                <button
                  type="submit"
                  className="flex-1 bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded-lg transition-colors duration-200"
                >
                  {editingId ? t('common.update') : t('hourly.addBlock')}
                </button>
                <button
                  type="button"
                  onClick={() => {
                    setShowAddForm(false);
                    setEditingId(null);
                    setNewPlan({ title: '', description: '', startTime: '', endTime: '', priority: 'medium' });
                  }}
                  className="flex-1 bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded-lg transition-colors duration-200"
                >
                  {t('common.cancel')}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <div className="lg:col-span-1">
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6">
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
              {t('hourly.hourlySchedule')}
            </h3>
            <div className="space-y-2 max-h-96 overflow-y-auto">
              {hours.map((hour) => {
                const hourPlans = dayPlans.filter(plan =>
                  plan.startTime && plan.startTime.toTimeString().substr(0, 5) === hour
                );

                return (
                  <button
                    key={hour}
                    onClick={() => {
                      setNewPlan({
                        ...newPlan,
                        startTime: hour,
                        endTime: hour.split(':')[0] + ':' + (parseInt(hour.split(':')[0]) + 1).toString().padStart(2, '0') + ':00'
                      });
                      setShowAddForm(true);
                    }}
                    className="w-full flex items-center gap-3 p-2 hover:bg-gray-50 dark:hover:bg-gray-700 rounded-lg transition-colors duration-200 text-left"
                  >
                    <span className="text-sm font-mono text-gray-600 dark:text-gray-400 w-12">
                      {hour}
                    </span>
                    <div className="flex-1">
                      {hourPlans.length > 0 ? (
                        <div className="space-y-1">
                          {hourPlans.map(plan => (
                            <div key={plan.id} className="text-sm text-gray-900 dark:text-white font-medium">
                              {plan.title}
                            </div>
                          ))}
                        </div>
                      ) : (
                        <span className="text-sm text-gray-400 dark:text-gray-500">{t('hourly.available')}</span>
                      )}
                    </div>
                  </button>
                );
              })}
            </div>
          </div>
        </div>

        <div className="lg:col-span-2">
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6">
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
              {t('hourly.dailyTimeBlocks')} - {selectedDate.toLocaleDateString(language)}
            </h3>

            {dayPlans.length === 0 ? (
              <div className="text-center py-12">
                <Clock className="mx-auto text-gray-400 dark:text-gray-500 mb-4" size={48} />
                <p className="text-gray-500 dark:text-gray-400 mb-4">
                  {t('hourly.noBlocks')}
                </p>
                <button
                  onClick={() => setShowAddForm(true)}
                  className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-2 rounded-lg transition-colors duration-200"
                >
                  {t('hourly.addFirstBlock')}
                </button>
              </div>
            ) : (
              <div className="space-y-4">
                {dayPlans.map((plan) => (
                  <div
                    key={plan.id}
                    className={`p-4 rounded-lg border-l-4 ${getPriorityColor(plan.priority)} transition-all duration-200 hover:shadow-md`}
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center gap-3 mb-2">
                          <button
                            onClick={() => updatePlan(plan.id, { completed: !plan.completed })}
                            className="text-blue-500 hover:text-blue-600 transition-colors duration-200"
                          >
                            {plan.completed ? <CheckCircle size={20} /> : <Circle size={20} />}
                          </button>
                          <h4 className={`text-lg font-semibold ${plan.completed ? 'line-through text-gray-500' : 'text-gray-900 dark:text-white'}`}>
                            {plan.title}
                          </h4>
                        </div>

                        {(plan.startTime || plan.endTime) && (
                          <div className="text-sm text-gray-600 dark:text-gray-400 mb-2 flex items-center gap-2">
                            <Clock size={16} />
                            {plan.startTime?.toTimeString().substr(0, 5)}
                            {plan.startTime && plan.endTime && ' - '}
                            {plan.endTime?.toTimeString().substr(0, 5)}
                          </div>
                        )}

                        {plan.description && (
                          <LinkifiedText
                            text={plan.description}
                            className="text-gray-700 dark:text-gray-300 mb-2"
                          />
                        )}

                        <div className="flex items-center gap-2">
                          <span className={`text-xs px-2 py-1 rounded-full ${plan.priority === 'high' ? 'bg-red-100 text-red-800 dark:bg-red-900/20 dark:text-red-400' :
                            plan.priority === 'medium' ? 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900/20 dark:text-yellow-400' :
                              'bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-400'
                            }`}>
                            {plan.priority === 'high' ? t('hourly.highPriority') : plan.priority === 'medium' ? t('hourly.mediumPriority') : t('hourly.lowPriority')}
                          </span>
                        </div>
                      </div>

                      <div className="flex items-center gap-2 ml-4">
                        <button
                          onClick={() => handleEdit(plan)}
                          className="p-2 text-gray-500 hover:text-blue-500 hover:bg-blue-50 dark:hover:bg-blue-900/20 rounded-lg transition-all duration-200"
                        >
                          <Edit2 size={16} />
                        </button>
                        <button
                          onClick={() => deletePlan(plan.id)}
                          className="p-2 text-gray-500 hover:text-red-500 hover:bg-red-50 dark:hover:bg-red-900/20 rounded-lg transition-all duration-200"
                        >
                          <Trash2 size={16} />
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

