import React, { useState } from 'react';
import { CalendarRange, ChevronLeft, ChevronRight, Pencil, Trash2, CheckCircle, Circle } from 'lucide-react';
import { useData } from '../../contexts/DataContext';
import { PlanItem } from '../../types/planner';
import { useLanguage } from '../../contexts/LanguageContext';

const MonthlyView: React.FC = () => {
  const { t } = useLanguage();
  const { plans, addPlan, updatePlan, deletePlan } = useData();
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [showAddForm, setShowAddForm] = useState(false);
  const [selectedDay, setSelectedDay] = useState<Date | null>(null);
  const [editingPlan, setEditingPlan] = useState<PlanItem | null>(null);
  const [newPlan, setNewPlan] = useState({
    title: '',
    description: '',
    priority: 'medium' as 'low' | 'medium' | 'high',
  });

  const monthNames = [
    t('months.january'), t('months.february'), t('months.march'), t('months.april'), t('months.may'), t('months.june'),
    t('months.july'), t('months.august'), t('months.september'), t('months.october'), t('months.november'), t('months.december')
  ];

  const dayNamesShort = [
    t('days.short.monday'), t('days.short.tuesday'), t('days.short.wednesday'), t('days.short.thursday'),
    t('days.short.friday'), t('days.short.saturday'), t('days.short.sunday')
  ];

  const getDaysInMonth = (date: Date) => {
    const year = date.getFullYear();
    const month = date.getMonth();
    const firstDay = new Date(year, month, 1);
    const startDate = new Date(firstDay);
    const startDay = firstDay.getDay();

    // Adjust to Monday as first day
    startDate.setDate(startDate.getDate() - (startDay === 0 ? 6 : startDay - 1));

    const days = [];
    for (let i = 0; i < 42; i++) {
      const day = new Date(startDate);
      day.setDate(startDate.getDate() + i);
      days.push(day);
    }
    return days;
  };

  const navigateMonth = (direction: 'prev' | 'next') => {
    const newDate = new Date(currentMonth);
    newDate.setMonth(currentMonth.getMonth() + (direction === 'next' ? 1 : -1));
    setCurrentMonth(newDate);
  };

  const getPlansForDay = (date: Date) => {
    const dateStr = date.toISOString().split('T')[0];
    return plans.filter((plan: PlanItem) =>
      plan.date.toISOString().split('T')[0] === dateStr
    );
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (editingPlan) {
      updatePlan(editingPlan.id, {
        title: newPlan.title,
        description: newPlan.description,
        priority: newPlan.priority,
      });
    } else if (selectedDay) {
      addPlan({
        title: newPlan.title,
        description: newPlan.description,
        date: selectedDay,
        completed: false,
        priority: newPlan.priority,
        linkedNotes: [],
      });
    }

    resetForm();
  };

  const resetForm = () => {
    setNewPlan({ title: '', description: '', priority: 'medium' });
    setShowAddForm(false);
    setSelectedDay(null);
    setEditingPlan(null);
  };

  const handleEditPlan = (plan: PlanItem, e: React.MouseEvent) => {
    e.stopPropagation();
    setEditingPlan(plan);
    setNewPlan({
      title: plan.title,
      description: plan.description || '',
      priority: plan.priority,
    });
    setShowAddForm(true);
  };

  const handleDeletePlan = (planId: string, e: React.MouseEvent) => {
    e.stopPropagation();
    if (window.confirm(t('common.confirmDelete') || 'Biztosan törölni szeretnéd?')) {
      deletePlan(planId);
    }
  };

  const days = getDaysInMonth(currentMonth);
  const isCurrentMonth = (date: Date) => date.getMonth() === currentMonth.getMonth();
  const isToday = (date: Date) => date.toDateString() === new Date().toDateString();

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <div className="mb-8">
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4 mb-6">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white flex items-center gap-3">
              <CalendarRange className="text-orange-500" size={32} />
              {t('monthly.title')}
            </h1>
            <p className="text-gray-600 dark:text-gray-400 mt-2">
              {t('monthly.subtitle')}
            </p>
          </div>

          <div className="flex items-center gap-4">
            <button
              onClick={() => navigateMonth('prev')}
              className="p-3 text-gray-600 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors duration-200"
            >
              <ChevronLeft size={20} />
            </button>

            <div className="text-center min-w-48">
              <div className="text-xl font-bold text-gray-900 dark:text-white">
                {monthNames[currentMonth.getMonth()]} {currentMonth.getFullYear()}
              </div>
            </div>

            <button
              onClick={() => navigateMonth('next')}
              className="p-3 text-gray-600 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors duration-200"
            >
              <ChevronRight size={20} />
            </button>
          </div>
        </div>
      </div>

      {/* Add/Edit Form Modal */}
      {showAddForm && (selectedDay || editingPlan) && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white dark:bg-gray-800 rounded-xl p-6 w-full max-w-md shadow-2xl">
            <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">
              {editingPlan ? t('common.edit') || 'Szerkesztés' : t('weekly.addTask')}
              {selectedDay && !editingPlan && ` - ${selectedDay.toLocaleDateString()}`}
            </h3>

            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  {t('daily.taskTitle')}
                </label>
                <input
                  type="text"
                  value={newPlan.title}
                  onChange={(e) => setNewPlan({ ...newPlan, title: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:ring-2 focus:ring-orange-500"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  {t('daily.taskDescription')}
                </label>
                <textarea
                  value={newPlan.description}
                  onChange={(e) => setNewPlan({ ...newPlan, description: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:ring-2 focus:ring-orange-500"
                  rows={3}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  {t('daily.priority')}
                </label>
                <select
                  value={newPlan.priority}
                  onChange={(e) => setNewPlan({ ...newPlan, priority: e.target.value as 'low' | 'medium' | 'high' })}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:ring-2 focus:ring-orange-500"
                >
                  <option value="low">{t('daily.lowPriority')}</option>
                  <option value="medium">{t('daily.mediumPriority')}</option>
                  <option value="high">{t('daily.highPriority')}</option>
                </select>
              </div>

              <div className="flex gap-3 pt-4">
                <button
                  type="submit"
                  className="flex-1 bg-orange-500 hover:bg-orange-600 text-white py-2 px-4 rounded-lg transition-colors duration-200"
                >
                  {editingPlan ? t('common.save') || 'Mentés' : t('weekly.addTask')}
                </button>
                <button
                  type="button"
                  onClick={resetForm}
                  className="flex-1 bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded-lg transition-colors duration-200"
                >
                  {t('common.cancel')}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg overflow-hidden">
        <div className="grid grid-cols-7 gap-px bg-gray-200 dark:bg-gray-600">
          {dayNamesShort.map((day) => (
            <div key={day} className="bg-gray-100 dark:bg-gray-700 p-3 text-center">
              <span className="text-sm font-semibold text-gray-700 dark:text-gray-300">{day}</span>
            </div>
          ))}
        </div>

        <div className="grid grid-cols-7 gap-px bg-gray-200 dark:bg-gray-600">
          {days.map((day) => {
            const dayPlans = getPlansForDay(day);
            const completedTasks = dayPlans.filter((plan: PlanItem) => plan.completed).length;

            return (
              <div
                key={day.toISOString()}
                className={`min-h-32 p-2 bg-white dark:bg-gray-800 hover:bg-gray-50 dark:hover:bg-gray-700 transition-colors duration-200 cursor-pointer ${!isCurrentMonth(day) ? 'opacity-40' : ''
                  } ${isToday(day) ? 'bg-purple-50 dark:bg-purple-900/10' : ''}`}
                onClick={() => {
                  setSelectedDay(day);
                  setEditingPlan(null);
                  setShowAddForm(true);
                }}
              >
                <div className={`text-sm font-medium mb-2 ${isToday(day) ? 'text-purple-600 dark:text-purple-400' :
                  isCurrentMonth(day) ? 'text-gray-900 dark:text-white' : 'text-gray-400 dark:text-gray-500'
                  }`}>
                  {day.getDate()}
                </div>

                <div className="space-y-1">
                  {dayPlans.slice(0, 2).map((plan: PlanItem) => (
                    <div
                      key={plan.id}
                      className={`text-xs p-1 rounded truncate cursor-pointer group relative ${plan.priority === 'high' ? 'bg-red-100 text-red-800 dark:bg-red-900/20 dark:text-red-400' :
                        plan.priority === 'medium' ? 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900/20 dark:text-yellow-400' :
                          'bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-400'
                        } ${plan.completed ? 'opacity-60 line-through' : ''}`}
                      onClick={(e) => handleEditPlan(plan, e)}
                    >
                      <div className="flex items-center gap-1">
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            updatePlan(plan.id, { completed: !plan.completed });
                          }}
                          className={`${plan.priority === 'high' ? 'hover:text-red-900 dark:hover:text-red-300' :
                            plan.priority === 'medium' ? 'hover:text-yellow-900 dark:hover:text-yellow-300' :
                              'hover:text-green-900 dark:hover:text-green-300'} transition-colors`}
                        >
                          {plan.completed ? <CheckCircle size={10} /> : <Circle size={10} />}
                        </button>
                        <span className="truncate flex-1">{plan.title}</span>
                        <div className="hidden group-hover:flex items-center gap-1">
                          <button
                            onClick={(e) => handleDeletePlan(plan.id, e)}
                            className="hover:text-red-600 p-0.5 rounded"
                          >
                            <Trash2 size={10} />
                          </button>
                        </div>
                      </div>
                    </div>
                  ))}

                  {dayPlans.length > 2 && (
                    <div className="text-xs text-gray-500 dark:text-gray-400">
                      +{dayPlans.length - 2} {t('common.more')}
                    </div>
                  )}
                </div>

                {dayPlans.length > 0 && (
                  <div className="text-xs text-gray-500 dark:text-gray-400 mt-2">
                    {completedTasks}/{dayPlans.length}
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default MonthlyView;

import React, { useState, useEffect, useRef } from 'react';
import { Play, Pause, RotateCcw, Timer, Coffee, Target } from 'lucide-react';
import { useLanguage } from '../../contexts/LanguageContext';
import { useNotifications } from '../../hooks/useNotifications';

type PomodoroMode = 'work' | 'shortBreak' | 'longBreak';

const PomodoroView: React.FC = () => {
  const { t } = useLanguage();
  const { permission, requestPermission, showNotification } = useNotifications();

  const [mode, setMode] = useState<PomodoroMode>('work');
  const [timeLeft, setTimeLeft] = useState(25 * 60); // 25 minutes in seconds
  const [isRunning, setIsRunning] = useState(false);
  const [completedPomodoros, setCompletedPomodoros] = useState(0);
  const [todayPomodoros, setTodayPomodoros] = useState(0);

  const intervalRef = useRef<NodeJS.Timeout | null>(null);

  const durations = {
    work: 25 * 60,
    shortBreak: 5 * 60,
    longBreak: 15 * 60,
  };

  // Load today's pomodoros from localStorage
  useEffect(() => {
    const today = new Date().toDateString();
    const savedData = localStorage.getItem('pomodoro-stats');
    if (savedData) {
      const stats = JSON.parse(savedData);
      if (stats.date === today) {
        setTodayPomodoros(stats.count || 0);
      } else {
        // New day, reset
        localStorage.setItem('pomodoro-stats', JSON.stringify({ date: today, count: 0 }));
      }
    }
  }, []);

  // Request notification permission on mount
  useEffect(() => {
    if (permission === 'default') {
      requestPermission();
    }
  }, [permission, requestPermission]);

  useEffect(() => {
    if (isRunning && timeLeft > 0) {
      intervalRef.current = setInterval(() => {
        setTimeLeft((prev) => prev - 1);
      }, 1000);
    } else if (timeLeft === 0) {
      handleTimerComplete();
    }

    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [isRunning, timeLeft]);

  const handleTimerComplete = () => {
    setIsRunning(false);

    // Play sound
    playSound();

    // Show notification
    if (mode === 'work') {
      showNotification({
        title: '🎉 Pomodoro Complete!',
        body: 'Great work! Time for a break.',
        requireInteraction: true,
      });

      // Update stats
      const newCount = todayPomodoros + 1;
      setTodayPomodoros(newCount);
      setCompletedPomodoros(completedPomodoros + 1);

      const today = new Date().toDateString();
      localStorage.setItem('pomodoro-stats', JSON.stringify({ date: today, count: newCount }));

      // Auto-switch to break
      if ((completedPomodoros + 1) % 4 === 0) {
        switchMode('longBreak');
      } else {
        switchMode('shortBreak');
      }
    } else {
      showNotification({
        title: '☕ Break Complete!',
        body: 'Ready to start another pomodoro?',
        requireInteraction: true,
      });
      switchMode('work');
    }
  };

  const playSound = () => {
    // Create a simple beep sound using Web Audio API
    const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
    const oscillator = audioContext.createOscillator();
    const gainNode = audioContext.createGain();

    oscillator.connect(gainNode);
    gainNode.connect(audioContext.destination);

    oscillator.frequency.value = 800;
    oscillator.type = 'sine';

    gainNode.gain.setValueAtTime(0.3, audioContext.currentTime);
    gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + 0.5);

    oscillator.start(audioContext.currentTime);
    oscillator.stop(audioContext.currentTime + 0.5);
  };

  const switchMode = (newMode: PomodoroMode) => {
    setMode(newMode);
    setTimeLeft(durations[newMode]);
    setIsRunning(false);
  };

  const toggleTimer = () => {
    setIsRunning(!isRunning);
  };

  const resetTimer = () => {
    setIsRunning(false);
    setTimeLeft(durations[mode]);
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const progress = ((durations[mode] - timeLeft) / durations[mode]) * 100;

  const getModeLabel = () => {
    if (mode === 'work') return t('pomodoro.focusTime');
    if (mode === 'shortBreak') return t('pomodoro.shortBreak');
    return t('pomodoro.longBreak');
  };

  return (
    <div className="p-3 md:p-6 max-w-6xl mx-auto">
      {/* Header */}
      <div className="mb-4 md:mb-6">
        <h1 className="text-2xl md:text-3xl font-bold text-gray-900 dark:text-white flex items-center gap-2">
          <Timer className="text-red-500" size={28} />
          {t('pomodoro.title')}
        </h1>
        <p className="text-sm md:text-base text-gray-600 dark:text-gray-400 mt-1 md:mt-2">
          {t('pomodoro.subtitle')}
        </p>
      </div>

      {/* Two-column layout for laptop */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-4 md:gap-6">

        {/* Left Column - Timer & Controls */}
        <div className="lg:col-span-2 space-y-4">

          {/* Stats Row */}
          <div className="grid grid-cols-2 gap-3">
            <div className="bg-gradient-to-br from-red-500 to-red-600 rounded-xl p-4 text-white">
              <div className="flex items-center gap-2 mb-1">
                <Target size={16} />
                <span className="text-xs opacity-90">{t('pomodoro.today')}</span>
              </div>
              <div className="text-2xl font-bold">{todayPomodoros}</div>
              <div className="text-xs opacity-90">{t('pomodoro.pomodoros')}</div>
            </div>

            <div className="bg-gradient-to-br from-blue-500 to-blue-600 rounded-xl p-4 text-white">
              <div className="flex items-center gap-2 mb-1">
