interface LanguageContextType {
  language: Language;
  setLanguage: (lang: Language) => void;
  changeLanguage: (lang: Language) => void;
  t: (key: string) => string;
}

const LanguageContext = createContext<LanguageContextType | undefined>(undefined);

export const LanguageProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const validLanguages: Language[] = ['en', 'hu', 'ro', 'sk', 'hr', 'de', 'fr', 'es', 'it', 'pl', 'cn', 'jp', 'pt', 'tr', 'ar', 'ru', 'hi', 'bn', 'ur', 'th', 'id'];

  const [language, setLanguage] = useState<Language>(() => {
    const saved = localStorage.getItem('language');
    const initialLang = (saved && validLanguages.includes(saved as Language)) ? (saved as Language) : 'en';
    document.documentElement.dir = languageDirections[initialLang] || 'ltr';
    return initialLang;
  });

  useEffect(() => {
    localStorage.setItem('language', language);
  }, [language]);

  const changeLanguage = (lang: Language) => {
    setLanguage(lang);
    const dir = languageDirections[lang];
    document.documentElement.dir = dir;
    document.documentElement.lang = lang;
  };

  const t = (key: string): string => {
    // 1. Check if translation key exists
    if (!translations[key]) {
      // Fallback: return the last part of the key (better than crashing or showing nothing)
      return key.split('.').pop() || key;
    }

    // 2. Try strict match for current language
    const strictMatch = translations[key][language];
    if (strictMatch) return strictMatch;

    // 3. Fallback to English (Global Standard)
    const enFallback = translations[key]['en'];
    if (enFallback) return enFallback;

    // 4. Absolute Fallback: return key itself if everything fails
    return key;
  };

  return (
    <LanguageContext.Provider value={{ language, setLanguage, changeLanguage, t }}>
      {children}
    </LanguageContext.Provider>
  );
};

export const useLanguage = () => {
  const context = useContext(LanguageContext);
  if (context === undefined) {
    throw new Error('useLanguage must be used within a LanguageProvider');
  }
  return context;
};